import { Client, ClientOptions, Guild, TextChannel, User } from "discord.js";
import { RawGuildChannelData, RawGuildData, RawUserData } from "discord.js/typings/rawDataTypes";

export const Mock = {
  Client: function (options: ClientOptions): Client {
    return new Client(options)
  },
  Guild: function (client: Client, data: RawGuildData): Guild {
    // @ts-ignore
    return new Guild(client, data);
  },
  TextChannel: function (guild: Guild, data?: RawGuildChannelData, client?: Client, immediatePatch?: boolean): TextChannel {
    // @ts-ignore
    return new TextChannel(guild, data);
  },
  User: function (client: Client, data?: RawUserData): User {
    // @ts-ignore
    return new User(client, data);
  }
}
